package com.visionvera.psychologist.c.module.counselling.adapter;import android.widget.ImageView;import com.bumptech.glide.Glide;import com.chad.library.adapter.base.BaseMultiItemQuickAdapter;import com.chad.library.adapter.base.viewholder.BaseViewHolder;import com.visionvera.psychologist.c.R;import com.visionvera.psychologist.c.module.usercenter.bean.FeedBackImgBean;import java.util.List;/** * @Desc 图片列表适配器 * @Author yemh * @Date 2019-11-01 13:32 */public class ImgAdapter extends BaseMultiItemQuickAdapter<FeedBackImgBean, BaseViewHolder> {    public ImgAdapter(List<FeedBackImgBean> data) {        super(data);        addItemType(FeedBackImgBean.IMG, R.layout.evaluation_module_img);    }    @Override    protected void convert(BaseViewHolder helper, FeedBackImgBean bean) {        switch (helper.getItemViewType()) {            case FeedBackImgBean.IMG:                Glide.with(getContext())                        .load(bean.getPicPath())                        .dontAnimate()                        .placeholder(R.mipmap.logo)                        .into((ImageView) helper.getView(R.id.iv));                break;            default:                break;        }    }}