package com.visionvera.psychologist.c.module.usercenter.adapter;import android.text.TextUtils;import android.view.View;import android.widget.ImageView;import com.bumptech.glide.Glide;import com.chad.library.adapter.base.BaseMultiItemQuickAdapter;import com.chad.library.adapter.base.viewholder.BaseViewHolder;import com.visionvera.psychologist.c.R;import com.visionvera.psychologist.c.module.usercenter.bean.FeedBackImgBean;import java.util.List;/** * @Desc 意见反截图适配器 * @Author yemh * @Date 2019-11-01 13:32 */public class FeedBackImgAdapter extends BaseMultiItemQuickAdapter<FeedBackImgBean, BaseViewHolder> {    public FeedBackImgAdapter(List<FeedBackImgBean> data) {        super(data);        addItemType(FeedBackImgBean.IMG, R.layout.evaluation_module_feedback_img_add);        addItemType(FeedBackImgBean.CAMERA, R.layout.evaluation_module_feedback_img_add);    }    @Override    protected void convert(BaseViewHolder helper, FeedBackImgBean bean) {        switch (helper.getItemViewType()) {            case FeedBackImgBean.IMG:                helper.getView(R.id.evaluation_module_feedback_iv_delete).setVisibility(View.VISIBLE);                Glide.with(getContext())                        .load(bean.getPicPath())                        .dontAnimate()                        .into((ImageView) helper.getView(R.id.evaluation_module_feedback_iv));                break;            case FeedBackImgBean.CAMERA:                helper.getView(R.id.evaluation_module_feedback_iv_delete).setVisibility(View.INVISIBLE);                if (TextUtils.isEmpty(bean.getPicPath())){                    ((ImageView) helper.getView(R.id.evaluation_module_feedback_iv)).setImageResource(R.drawable.evaluation_module_add);                }                break;            default:        }    }}