package com.visionvera.psychologist.c.module.usercenter.adapter;import android.content.Context;import android.widget.ImageView;import android.widget.TextView;import androidx.annotation.Nullable;import com.chad.library.adapter.base.BaseQuickAdapter;import com.chad.library.adapter.base.viewholder.BaseViewHolder;import com.visionvera.psychologist.c.R;import com.visionvera.psychologist.c.module.usercenter.bean.MessageBean;import java.util.List;/** * @Desc 我的消息适配器 * @Author yemh * @Date 2019-11-01 13:32 */public class MsgAdapter extends BaseQuickAdapter<MessageBean.ResultBean.DataListBean, BaseViewHolder> {    private Context mContext;    private ImageView ivNewMsg;    private TextView tvMsgTitle;    private TextView tvMsgDate;    private TextView tvMsgContent;    public MsgAdapter(Context context, @Nullable List<MessageBean.ResultBean.DataListBean> data) {        super(R.layout.evaluation_module_msg_item, data);        this.mContext = context;    }    @Override    protected void convert(BaseViewHolder holder, MessageBean.ResultBean.DataListBean bean) {        ivNewMsg = holder.getView(R.id.iv_new_msg);        tvMsgTitle = holder.getView(R.id.tv_msg_title);        tvMsgDate = holder.getView(R.id.tv_msg_date);        tvMsgContent = holder.getView(R.id.tv_msg_content);        tvMsgTitle.setText(bean.getName());        tvMsgContent.setText(bean.getDescription());    }}