package com.visionvera.live.base.event;/** * @Desc 自定义EventBus消息类 * @Author yemh * @Date 2019/4/12 15:45 */public class MessageEvent {    private String message;    private int type;    private Object msg;    private Object[] messages;    public MessageEvent(int type) {        this.type = type;    }    public MessageEvent(int type, String msg) {        this.type = type;        this.message = msg;    }    public MessageEvent(int type, Object message) {        this.type = type;        this.msg = message;    }    public MessageEvent(int type, Object... msg) {        this.type = type;        this.messages = msg;    }    public int getType() {        return type;    }    public void setType(int type) {        this.type = type;    }    public String getMessage() {        return message;    }    public Object getMsg() {        return msg;    }    public void setMsg(Object msg) {        this.msg = msg;    }    public Object[] getMessages() {        return messages;    }    public void setMessage(String message) {        this.message = message;    }}