package com.visionvera.live.module.collect.adapter;import android.content.Context;import android.text.TextUtils;import android.widget.ImageView;import android.widget.TextView;import com.visionvera.library.util.GlideImageLoader;import com.visionvera.live.R;import com.visionvera.live.module.home.bean.CourseBean;import com.visionvera.live.widget.recycler.BaseQuickAdapter;import com.visionvera.live.widget.recycler.BaseViewHolder;/** * @Desc 未开始列表适配器 * @Author yemh * @Date 2019/6/17 15:12 */public class NoStartedLiveAdapter extends BaseQuickAdapter<CourseBean, BaseViewHolder> {    private Context mContext;    private ImageView ivLiveShortCut;    private TextView tvLiveType;    private TextView tvTitle;    private TextView tvExpert;    private TextView tvLiveTime;    public NoStartedLiveAdapter(Context context) {        super(R.layout.item_recy_collect_layout1, null);        this.mContext = context;    }    @Override    protected void convert(BaseViewHolder holder, CourseBean bean) {        ivLiveShortCut = holder.getView(R.id.iv_live_shaortcut);        tvLiveType = holder.getView(R.id.tv_type);        tvTitle = holder.getView(R.id.tv_title);        tvExpert = holder.getView(R.id.tv_expert);        tvLiveTime = holder.getView(R.id.tv_live_time);        tvTitle = holder.getView(R.id.tv_title);        tvTitle.setText(bean.getCourseName());        tvExpert.setText(bean.getExpertName());        String url = bean.getMasterPicUrl();        if (!TextUtils.isEmpty(url)) {            GlideImageLoader.loadImage(mContext, url, ivLiveShortCut);        }        int playModel = bean.getPlayModel();        if (playModel == 1) {            tvLiveType.setText(R.string.record);        } else {            tvLiveType.setText(R.string.live);        }        tvLiveTime.setText(bean.getStartTime());    }}