package com.visionvera.live.module.collect.model;import com.trello.rxlifecycle2.LifecycleProvider;import com.visionvera.live.base.bean.ResBean;import com.visionvera.live.module.collect.contract.CollectContract;import com.visionvera.live.network.HttpHelper;import java.util.Map;import io.reactivex.Observer;/** * @Desc 收藏和取消收藏的model实现类 * * @Author yemh * @Date 2019/4/15 17:42 */public class CollectModel implements CollectContract.ICollect.IModel {    @Override    public void collectResult(Map<String, String> params, LifecycleProvider provider, Observer<ResBean> observer) {        HttpHelper.getInstance().getCollectResult(params, provider, observer);    }    @Override    public void cancelCollectResult(Map<String, String> params, LifecycleProvider provider, Observer<ResBean> observer) {        HttpHelper.getInstance().getCancelCollectResult(params, provider, observer);    }}