package com.visionvera.live.module.collect.presenter;import android.content.Context;import com.trello.rxlifecycle2.LifecycleProvider;import com.visionvera.live.base.bean.ResBean;import com.visionvera.live.module.collect.contract.CollectContract;import com.visionvera.live.module.collect.model.CollectListModel;import com.visionvera.live.module.home.bean.CourseBean;import com.visionvera.live.network.helper.ApiException;import com.visionvera.live.network.helper.HttpRxObserver;import java.util.List;import okhttp3.RequestBody;/** * @Desc 收藏列表的presenter实现类 * * @Author yemh * @Date 2019/4/15 17:35 */public class CollectListPresenter implements CollectContract.ICollectList.IPresenter {    CollectContract.ICollectList.IModel mModel;    CollectContract.ICollectList.IView mView;    public CollectListPresenter(CollectContract.ICollectList.IView mView) {        this.mView = mView;        mModel = new CollectListModel();    }    @Override    public void getCollectList(Context context, RequestBody params, LifecycleProvider provider) {        mModel.collectListResult(params, provider, new HttpRxObserver<ResBean<List<CourseBean>>>(context, true) {            @Override            protected void onError(ApiException e) {                mView.showError(e.getMessage());            }            @Override            protected void onSuccess(ResBean<List<CourseBean>> data) {                mView.showCollectListResult(data);            }        });    }}