package com.visionvera.live.module.home.adapter;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.widget.LinearLayout;import android.widget.TextView;import com.visionvera.live.R;import com.visionvera.live.module.home.bean.CalendarListBean;import com.visionvera.live.module.home.bean.MeetingExpertListBean;import com.visionvera.live.module.home.bean.MettingScheduleBean;import com.visionvera.live.widget.recycler.BaseQuickAdapter;import com.visionvera.live.widget.recycler.BaseViewHolder;import java.util.List;/** * @Desc 日程安排列表适配器 * @Author yemh * @Date 2019/6/17 15:12 */public class MettingScheduleAdapter extends BaseQuickAdapter<MettingScheduleBean, BaseViewHolder> {    private Context mContext;    private TextView tvDate;    private LinearLayout llChildSchedule;    private LayoutInflater mInflater;    public MettingScheduleAdapter(Context context) {        super(R.layout.item_schedule_layout, null);        this.mContext = context;        mInflater = LayoutInflater.from(mContext);    }    @Override    protected void convert(BaseViewHolder holder, MettingScheduleBean bean) {        tvDate = holder.getView(R.id.tv_date);        llChildSchedule = holder.getView(R.id.ll_child_schedule);        tvDate.setText(bean.getCalendarDate());        List<CalendarListBean> calendarList = bean.getCalendarList();        for (int i = 0; i < calendarList.size(); i++) {            View childView = mInflater.inflate(R.layout.item_schedule_childl_ayout, null);            TextView tvTime = childView.findViewById(R.id.tv_time);            TextView tvMainSchedule = childView.findViewById(R.id.tv_main_schedule);            TextView tvChildSchedule = childView.findViewById(R.id.tv_child_schedule);            CalendarListBean vo = calendarList.get(i);            tvTime.setText(vo.getStartDate() + "-" + vo.getEndDate());            tvMainSchedule.setText(vo.getName());            List<MeetingExpertListBean> meetingExpertList = vo.getMeetingExpertList();            for (int j = 0; j < meetingExpertList.size(); j++) {                MeetingExpertListBean iVo = meetingExpertList.get(j);                tvChildSchedule.setText(iVo.getExpertName() + "(" + iVo.getExpertInfo() + ")");            }            llChildSchedule.addView(childView);        }    }}