package com.visionvera.live.module.home.model;import com.trello.rxlifecycle2.LifecycleProvider;import com.visionvera.live.base.bean.ResBean;import com.visionvera.live.module.home.bean.AddChatterBean;import com.visionvera.live.module.home.bean.ChatMessageBean;import com.visionvera.live.module.home.bean.GroupChatterBean;import com.visionvera.live.module.home.bean.GroupIdBean;import com.visionvera.live.module.home.contract.Contract;import com.visionvera.live.network.HttpHelper;import java.util.List;import java.util.Map;import io.reactivex.Observer;import okhttp3.RequestBody;/** * @Desc IM的model实现类 * * @Author yemh * @Date 2019/4/15 17:42 */public class ImModel implements Contract.IIm.IModel {    @Override    public void getGroupByBusinessIdResult(Map<String, Integer> params, LifecycleProvider provider, Observer<ResBean<GroupIdBean>> observer) {        HttpHelper.getInstance().getGroupByBusinessId(params, provider, observer);    }    @Override    public void addChatterResult(RequestBody params, LifecycleProvider provider, Observer<ResBean<AddChatterBean>> observer) {        HttpHelper.getInstance().addChatter(params, provider, observer);    }    @Override    public void getHistoryMessageResult(String url, RequestBody params, LifecycleProvider provider, Observer<ResBean<List<ChatMessageBean>>> observer) {        HttpHelper.getInstance().getHistoryMessage(url, params, provider, observer);    }    @Override    public void getGroupChatterResult(RequestBody params, LifecycleProvider provider, Observer<ResBean<List<GroupChatterBean>>> observer) {        HttpHelper.getInstance().getGroupChatter(params, provider, observer);    }}