package com.visionvera.live.module.home.presenter;import android.content.Context;import com.trello.rxlifecycle2.LifecycleProvider;import com.visionvera.live.base.bean.ResBean;import com.visionvera.live.base.mvp.IBaseView;import com.visionvera.live.module.home.bean.CourseBean;import com.visionvera.live.module.home.contract.Contract;import com.visionvera.live.module.home.model.BannerModel;import com.visionvera.live.network.helper.ApiException;import com.visionvera.live.network.helper.HttpRxObserver;import java.util.List;import java.util.Map;/** * @Desc 首页轮播图的presenter实现类 * * @Author yemh * @Date 2019/4/15 17:35 */public class BannerPresenter implements Contract.IBanner.IPresenter {    Contract.IBanner.IModel mModel;    Contract.IBanner.IView mView;    public BannerPresenter(Contract.IBanner.IView mView) {        this.mView = mView;        mModel = new BannerModel();    }    @Override    public void getBanner(Context context, Map<String, String> params, LifecycleProvider provider) {        mModel.bannerResult(params, provider, new HttpRxObserver<ResBean<List<CourseBean>>>(context, true) {            @Override            protected void onError(ApiException e) {                mView.showBannerResult(null, IBaseView.ResultType.FAIL);            }            @Override            protected void onSuccess(ResBean<List<CourseBean>> data) {                mView.showBannerResult(data, IBaseView.ResultType.SUCCESS);            }        });    }}