package com.visionvera.live.module.home.presenter;import android.content.Context;import com.trello.rxlifecycle2.LifecycleProvider;import com.visionvera.live.base.bean.ResBean;import com.visionvera.live.module.home.bean.CourseBean;import com.visionvera.live.module.home.contract.Contract;import com.visionvera.live.module.home.model.LiveModel;import com.visionvera.live.network.helper.ApiException;import com.visionvera.live.network.helper.HttpRxObserver;import java.util.List;import okhttp3.RequestBody;/** * @Desc 直播列表的presenter实现类 * * @Author yemh * @Date 2019/4/15 17:35 */public class LivePresenter implements Contract.ILive.IPresenter {    Contract.ILive.IModel mModel;    Contract.ILive.IView mView;    public LivePresenter(Contract.ILive.IView mView) {        this.mView = mView;        mModel = new LiveModel();    }    @Override    public void getSource(Context context, RequestBody params, LifecycleProvider provider) {        mModel.getSourceResult(params, provider, new HttpRxObserver<ResBean<List<CourseBean>>>(context, true) {            @Override            protected void onError(ApiException e) {                mView.showError(e.getMessage());            }            @Override            protected void onSuccess(ResBean<List<CourseBean>> data) {                mView.showSourceResult(data);            }        });    }}