package com.visionvera.live.module.home.presenter;import android.content.Context;import com.trello.rxlifecycle2.LifecycleProvider;import com.visionvera.live.base.bean.ResBean;import com.visionvera.live.module.home.bean.MettingScheduleBean;import com.visionvera.live.module.home.contract.Contract;import com.visionvera.live.module.home.model.MettingScheduleModel;import com.visionvera.live.network.helper.ApiException;import com.visionvera.live.network.helper.HttpRxObserver;import java.util.List;import java.util.Map;/** * @Desc 日程安排的presenter实现类 * * @Author yemh * @Date 2019/4/15 17:35 */public class MettingSchedulePresenter implements Contract.IMettingSchedule.IPresenter {    Contract.IMettingSchedule.IModel mModel;    Contract.IMettingSchedule.IView mView;    public MettingSchedulePresenter(Contract.IMettingSchedule.IView mView) {        this.mView = mView;        mModel = new MettingScheduleModel();    }    @Override    public void getMettingSchedule(Context context, Map<String, String> params, LifecycleProvider provider) {        mModel.getMettingScheduleResult(params, provider, new HttpRxObserver<ResBean<List<MettingScheduleBean>>>(context, true) {            @Override            protected void onError(ApiException e) {                mView.showError(e.getMessage());            }            @Override            protected void onSuccess(ResBean<List<MettingScheduleBean>> data) {                mView.showMettingScheduleResult(data);            }        });    }}