package com.visionvera.live.module.login.contract;import android.content.Context;import com.trello.rxlifecycle2.LifecycleProvider;import com.visionvera.live.base.bean.ResBean;import com.visionvera.live.base.mvp.IBaseModel;import com.visionvera.live.base.mvp.IBasePresenter;import com.visionvera.live.base.mvp.IBaseView;import com.visionvera.live.module.login.bean.LoginBean;import io.reactivex.Observer;/** * @author: 刘传政 * @date: 2019/4/18 09:25 * QQ:1052374416 * 作用:整个登录模块的mvp管理类 * 注意事项: */public interface LoginContract {    /**     * 登录页     */    interface ILogin {        interface IModel extends IBaseModel {            void loginResult(LifecycleProvider provider, Observer<ResBean<LoginBean>> observer);        }        interface IView extends IBaseView {            void showLoginResult(ResBean<LoginBean> bean);        }        interface IPresenter extends IBasePresenter {            void login(Context context, LifecycleProvider provider);        }    }}