package com.visionvera.live.module.login.presenter;import android.content.Context;import android.util.Log;import com.trello.rxlifecycle2.LifecycleProvider;import com.visionvera.live.base.bean.ResBean;import com.visionvera.live.module.login.bean.LoginBean;import com.visionvera.live.module.login.contract.LoginContract;import com.visionvera.live.module.login.model.LoginModel;import com.visionvera.live.network.helper.ApiException;import com.visionvera.live.network.helper.HttpRxObserver;/** * @Desc * * @Author yemh * @Date 2019-07-24 09:09 * */public class LoginPresenter implements LoginContract.ILogin.IPresenter {    private static final String TAG = "LoginPresenter";    LoginContract.ILogin.IModel mModel;    LoginContract.ILogin.IView mView;    public LoginPresenter(LoginContract.ILogin.IView mView) {        this.mView = mView;        mModel = new LoginModel();    }    @Override    public void login(Context context, LifecycleProvider provider) {        mModel.loginResult(provider, new HttpRxObserver<ResBean<LoginBean>>(context, true) {            @Override            protected void onError(ApiException e) {                Log.e(TAG, "onError: "+e );                mView.showError(e.getMessage());            }            @Override            protected void onSuccess(ResBean<LoginBean> data) {                Log.e(TAG, "onSuccess: "+data );                mView.showLoginResult(data);            }        });    }}