package com.visionvera.live.module.search.presenter;import android.content.Context;import com.trello.rxlifecycle2.LifecycleProvider;import com.visionvera.live.base.bean.ResBean;import com.visionvera.live.base.mvp.IBaseView;import com.visionvera.live.module.home.bean.CourseBean;import com.visionvera.live.module.search.contract.SearchContract;import com.visionvera.live.module.search.model.SearchModel;import com.visionvera.live.network.helper.ApiException;import com.visionvera.live.network.helper.HttpRxObserver;import java.util.List;import okhttp3.RequestBody;/** * @Desc 我的关注列表的presenter实现类 * * @Author yemh * @Date 2019/4/15 17:35 */public class SearchPresenter implements SearchContract.IPresenter {    SearchContract.IModel mModel;    SearchContract.IView mView;    public SearchPresenter(SearchContract.IView mView) {        this.mView = mView;        mModel = new SearchModel();    }    @Override    public void getSearchCourse(Context context, RequestBody params, LifecycleProvider provider) {        mModel.getSearchResult(params, provider, new HttpRxObserver<ResBean<List<CourseBean>>>(context, true) {            @Override            protected void onError(ApiException e) {                mView.showError(e.getMessage());            }            @Override            protected void onSuccess(ResBean<List<CourseBean>> data) {                mView.showSearchResult(data);            }        });    }}