package com.visionvera.live.network.helper;/** * @Desc 自定义运行时=服务器异常 * @Author yemh * @Date 2019/4/15 17:12 */public class ServerException extends RuntimeException {    private int code;    private String msg;    public ServerException(int code, String msg) {        this.code = code;        this.msg = msg;    }    public int getCode() {        return code;    }    public String getMsg() {        return msg;    }}